<template>
  <img :src="url" alt="志汇同城">
</template>

<script>
  const PlaceholderImg = "data:image/jpg;base64,";

  export default {
    name: "ImgWrapper",
    props: ["src", "preview", "prevent"],
    data() {
      return {
        url: PlaceholderImg
      }
    },
    methods: {
      changeImage(url) {
        if (!url) return;
        const image = new Image();
        image.src = url;
        image.onload = () => {
          this.url = url;
          image.onload = null;
        }
      }
    },
    computed: {},
    watch: {
      src(newVal) {
        this.changeImage(newVal);
      }
    },
    created() {
      this.changeImage(this.src);
    }
  }
</script>

